sys_script.script:
(function executeRule(current, previous /*null when async*/) {

		//Puede existir la necesidad de que los pool de trabajo "my work" y "my groups work" sea necesario NO visualizar determinados tipos de tarea, este es el caso de las "candidates validation requests" que son planificadas/trabajadas por medio de un "candidates validation plan", para este caso particular el campo "manual assignment treatment" tendrÃ¡ el valor de falso

	if(current.sys_class_name == 'x_attme_microwaves_candidate_validation_request')
	{
	    var cvr = new GlideRecord('x_attme_microwaves_candidate_validation_request');
		cvr.addQuery('sys_id', current.sys_id);
		cvr.query();

		if(cvr.next())
		{
			//CVR planificada en programa
			if(cvr.planned == "true")
			{
				current.manual_assignment_treatment = false; 
			}

			//CVR no planificada en programa
			if(cvr.planned == "false")
			{
				current.manual_assignment_treatment = true;
			}
		}
	}

	else
	{
		current.manual_assignment_treatment = true;
	}

})(current, previous);
