sys_ui_policy.script_false:
function onCondition() {
//si es un nuevo registro la secci贸n "historic information" se oculta
	if(g_form.isNewRecord())
	{
		g_form.setSectionDisplay("historic_information", false);
	}

//si el registro no es nuevo unicamente los usuarios fulfiller pueden ver la secci贸n
	if(!g_form.isNewRecord())
	{
		if(g_user.hasRole('x_attme_microwaves.MW user'))
		{  g_form.setSectionDisplay("historic_information", true);  }

		if(!g_user.hasRole('x_attme_microwaves.MW user'))
		{   g_form.setSectionDisplay("historic_information", false);  }
	}

}

